import React, {Component} from 'react';
import Select from 'react-select';
import CustomFileInput from '../CustomFileInput';
import '../../Benefits/Benefits.css';
import {main_url} from "../../../utils/CommonFunction";

class BenefitChildAddNew extends Component {
    constructor() {
        super();
        this.state = {
            childBenefitsData: {
                employeeName: [],
                designation: '',
                noOfChildren: '',
                attachment: '',
            },
            dataSource: []
        }
    }

    componentDidMount() {
        fetch(`${main_url}benefit/getEmployeeList`)
            .then(res => { if (res.ok) return res.json() })
            .then(list => {
                this.setState({
                    employeeNameList: list
                })
            })
    }

    handleEmployeeName = (event) => {
        let data = this.state.childBenefitsData;
        data.employeeName = event.label;
        this.setState({
            childBenefitsData: data
        });
    };

    handleDesignation = (event) => {
        let data = this.state.childBenefitsData;
        data.designation = event.target.value;
        this.setState({
            childBenefitsData: data
        });
    };

    handleNoOfChildren = (event) => {
        let data = this.state.childBenefitsData;
        data.noOfChildren = event.target.value;
        this.setState({
            childBenefitsData: data
        });
    };

    checkFiles(e) {
        var files = document.getElementById("attach_file").files;
        var attachment = [];
        if (files.length > 5) {
            toast.warning('You can only upload a maximum of 5 files!')
        }
        else {
            for (let i = 0; i < files.length; i++) {
                attachment.push(files[i])
            }
        }
        this.setState({
            attachment: attachment
        })
    }


    save = () => {
        let data = this.state.dataSource;
        data.push(this.state.childBenefitsData);

        this.setState({
            dataSource: data,

            childBenefitsData: {
                employeeName: '',
                designation: '',
                noOfChildren: '',

            }

        });

    };

    handleRemove = (event) => {
        let data = this.state.dataSource;
        data.splice(event, 1);
        this.setState({
            dataSource: data
        });
    };


    render() {
        return (
            <div className="benefits benefit-child-add-new">
                <div className='row'>
                    <form className="form-group">
                        <div className="col-md-12">
                            <div><label htmlFor="employee-name" className="col-sm-12">Employee Name</label></div>
                            <div className="col-sm-11">
                                <Select
                                    options={this.state.childBenefitsData.employeeName}
                                    placeholder="Please Choose The Employee Name"
                                    onChange={this.handleEmployeeName}
                                    value={this.state.childBenefitsData.employeeName}
                                />
                            </div>
                        </div>

                        <div className="col-md-6">
                            <div><label htmlFor="designation" className="col-sm-12">Designation</label></div>
                            <div className="col-sm-10">
                                <input
                                    type="text"
                                    placeholder="Please Provide The Designation"
                                    className="form-control"
                                    onChange={this.handleDesignation}
                                    value={this.state.childBenefitsData.designation}
                                />
                            </div>
                        </div>

                        <div className="col-md-6">
                            <div><label htmlFor="spouse-name" className="col-sm-12">Number Of Children</label></div>
                            <div className="col-sm-10">
                                <input
                                    type="number"
                                    placeholder="Please Provide The Number Of Your Children"
                                    className="form-control"
                                    onChange={this.handleNoOfChildren}
                                    value={this.state.childBenefitsData.noOfChildren}
                                />
                            </div>
                        </div>

                        <div className="col-12 col-sm-6 col-lg-3 col-xl-3">
                            <label>Attachment</label>
                            <input className="full_width" type="file" id="attach_file" multiple onChange={this.checkFiles.bind(this)}></input>
                        </div>


                    </form>

                </div>

                <div className="row save-btn">
                    <div className="float-right">
                        <div>
                            <button className="btn btn-info" type="button" onClick={this.save}>Save</button>
                        </div>

                    </div>
                </div>

                <hr/>

                <div className="row">
                    <div className="result-area col-md-12">
                        <table className="table table-bordered table-responsive">
                            <thead>
                            <tr>
                                <th>Employee Name</th>
                                <th>Designation</th>
                                <th>No Of Children</th>
                                <th>Action</th>
                            </tr>
                            </thead>


                            <tbody>

                            {
                                (this.state.dataSource.length <= 0) ?
                                    ( <tr><td colSpan="8" className="text-center font-weight-bold text-white bg-danger">
                                        No Data To Show
                                    </td></tr> ) :
                                    (this.state.dataSource.map((item, index) => {
                                            return(
                                                <tr key={index}>
                                                    <td>{item.employeeName}</td>
                                                    <td>{item.designation}</td>
                                                    <td>{item.noOfChildren}</td>
                                                    <td>
                                                        <button className="btn btn-danger" onClick={this.handleRemove.bind(this, index)}>Remove</button>
                                                    </td>
                                                </tr>
                                            );
                                        })

                                    )
                            }
                            </tbody>

                        </table>
                    </div>
                </div>

            </div>
        )
    }
}

export default BenefitChildAddNew;
