import React, {Component} from 'react';
import Select from 'react-select';
import CustomFileInput from '../CustomFileInput';
import '../../Benefits/Benefits.css';
import {main_url} from "../../../utils/CommonFunction";

const SpouseCompanyOptions = [
    {value: 1, label: 'Yes'},
    {value: 2, label: 'No'}
];

class BenefitWeddingAddNew extends Component {
    constructor() {
        super();
        this.state = {
            weddingBenefitsData: {
                employeeName: [],
                designation: '',
                spouseName: '',
                spouseCompanyOption: '',

            },
            dataSource: [],
            selectedSpouseCompany: ''
        }
    }

    componentDidMount() {

        fetch(`${main_url}staff_loan/getEmployeeList`)
            .then(res => { if (res.ok) return res.json() })
            .then(list => {
                this.setState({
                    employeeName: list
                })
            })
    }

    handleEmployeeName = (event) => {
        let data = this.state.weddingBenefitsData;
        data.employeeName = event.label;
        this.setState({
            weddingBenefitsData: data
        });
    };

    handleDesignation = (event) => {
        let data = this.state.weddingBenefitsData;
        data.designation = event.target.value;
        this.setState({
            weddingBenefitsData: data
        });
    };

    handleSpouseName = (event) => {
        let data = this.state.weddingBenefitsData;
        data.spouseName = event.target.value;
        this.setState({
            weddingBenefitsData: data
        });
    };

    handleSpouseCompanyOptions = (event) => {
        let data = this.state.weddingBenefitsData;
        data.spouseCompanyOption = event.label;
        this.setState({
            weddingBenefitsData: data,
            selectedSpouseCompany: event
        });
    };

    handleAttachment = (event) => {
        let data = this.state.weddingBenefitsData;
        data.attachment = event.target.value;
        this.setState({
            weddingBenefitsData: data
        });
    };

    save = () => {
        let data = this.state.dataSource;
        data.push(this.state.weddingBenefitsData);

        this.setState({
            dataSource: data,

            weddingBenefitsData: {
                employeeName: [],
                designation: '',
                spouseName: '',
                attachment: '',
            },

            selectedSpouseCompany: ''

        });

    };

    handleRemove = (event) => {
        let data = this.state.dataSource;
        data.splice(event, 1);
        this.setState({
            dataSource: data
        });
    };


    render() {
        return (
            <div className="container">
                <div className='row'>
                    <form>
                        <div className="form-group col-md-12">
                            <div><label htmlFor="employee-name" className="col-sm-12">Employee Name</label></div>
                            <div className="col-sm-11">
                                <Select
                                    options={this.state.employeeName}
                                    placeholder="Please Choose Employee Name"
                                    onChange={this.handleEmployeeName}
                                />
                            </div>
                        </div>

                        <div className="form-group col-md-6">
                            <div><label htmlFor="designation" className="col-sm-12">Designation</label></div>
                            <div className="col-sm-10">
                                <input
                                    type="text"
                                    placeholder="Please Provide The Designation"
                                    className="form-control"
                                    onChange={this.handleDesignation}
                                    value={this.state.weddingBenefitsData.designation}
                                />
                            </div>
                        </div>


                        <div className="form-group col-md-6">
                            <div><label htmlFor="spouse-name" className="col-sm-12">Spouse Name</label></div>
                            <div className="col-sm-10">
                                <input
                                    type="text"
                                    placeholder="Please Provide The Employee Name"
                                    className="form-control"
                                    onChange={this.handleSpouseName}
                                    value={this.state.weddingBenefitsData.spouseName}
                                />
                            </div>
                        </div>

                        <div className="form-group col-md-6">
                            <div><label htmlFor="employee-name" className="col-sm-12">Is Your Spouse An Alliance Staff
                                ?</label></div>
                            <div className="col-sm-10">
                                <Select
                                    placeholder="Please Choose An Option"
                                    options={SpouseCompanyOptions}
                                    onChange={this.handleSpouseCompanyOptions}
                                    value={this.state.selectedSpouseCompany}
                                />
                            </div>
                        </div>

                        <div className="form-group col-md-6">
                            <div><label htmlFor="attachment" className="col-sm-12 custom-file-label">Provide At Least One Or At Most Two
                                Attachment</label></div>
                            <div className="col-sm-10">

                                <CustomFileInput
                                    isMultiple="true"
                                    value={this.state.weddingBenefitsData.attachment}
                                    btnName="Upload"
                                    btnColor='#495057'
                                    btnBg='#e9ecef'
                                    onChange={this.handleAttachment}
                                />
                            </div>


                        </div>


                    </form>

                </div>

                <div className="row save-btn">
                    <div className="float-right">
                        <div>
                            <button className="btn btn-info" type="button" onClick={this.save}>Save</button>
                        </div>

                    </div>
                </div>

                <hr/>

                <div className="row">
                    <div className="result-area col-md-12">
                        <table className="table table-bordered table-responsive">
                            <thead>
                                <tr>
                                    <th>Employee Name</th>
                                    <th>Designation</th>
                                    <th>Spouse Name</th>
                                    <th>Is Spouse An Alliance Employee</th>
                                    <th>Action</th>
                                </tr>
                            </thead>


                            <tbody>

                                    {
                                        (this.state.dataSource.length <= 0) ?
                                            ( <tr><td colSpan="8" className="text-center font-weight-bold text-white bg-danger">
                                                No Data To Show
                                            </td></tr> ) :
                                            (this.state.dataSource.map((item, index) => {
                                                return(
                                                    <tr key={index}>
                                                        <td>{item.employeeName}</td>
                                                        <td>{item.designation}</td>
                                                        <td>{item.spouseName}</td>
                                                        <td>{item.spouseCompanyOption}</td>
                                                        <td>
                                                            <button className="btn btn-danger" onClick={this.handleRemove.bind(this, index)}>Remove</button>
                                                        </td>
                                                    </tr>
                                                );
                                            })

                                        )
                                    }
                            </tbody>

                        </table>
                    </div>
                </div>

            </div>
        )
    }
}

export default BenefitWeddingAddNew;
