import React, {Component} from 'react';
import Select from 'react-select';
import CustomFileInput from '../CustomFileInput';
import '../../Benefits/Benefits.css';
import {main_url} from "../../../utils/CommonFunction";

const deadPersonOptions = [
    {value: 1, label: 'Parent'},
    {value: 2, label: 'Children'},
    {value: 3, label: 'Sibling'},
    {value: 4, label: 'Employee Oneself'}
];

class BenefitFuneralAddNew extends Component {
    constructor() {
        super();
        this.state = {
            funeralBenefitsData: {
                employeeName: '',
                designation: '',
                spouseName: '',
                deadPerson: '',
                headNo: '',
                attachment: '',
            },
            dataSource: [],
            selectedDeadPerson: ''
        }
    }

    componentDidMount() {
        fetch(`${main_url}benefit/getEmployeeList`)
            .then(res => { if (res.ok) return res.json() })
            .then(list => {
                this.setState({
                    employeeNameList: list
                })
            })
    }

    handleEmployeeName = (event) => {
        let data = this.state.funeralBenefitsData;
        data.employeeName = event.label;
        this.setState({
            funeralBenefitsData: data
        });
    };

    handleDesignation = (event) => {
        let data = this.state.funeralBenefitsData;
        data.designation = event.target.value;
        this.setState({
            funeralBenefitsData: data
        });
    };

    handleDeadPersonOptions = (event) => {
        let data = this.state.funeralBenefitsData;
        data.deadPerson = event.label;
        this.setState({
            funeralBenefitsData: data,
            selectedDeadPerson: event
        });
    };

    handleHeadNo = (event) => {
        let data = this.state.funeralBenefitsData;
        data.headNo = event.target.value;

        this.setState({
            funeralBenefitsData: data
        });
    };

    handleAttachment = (event) => {
        let data = this.state.funeralBenefitsData;
        data.attachment = event.target.value;
        this.setState({
            funeralBenefitsData: data
        });
    };

    save = () => {
        let data = this.state.dataSource;
        data.push(this.state.funeralBenefitsData);

        this.setState({
            dataSource: data,

            funeralBenefitsData: {
                employeeName: '',
                designation: '',
                spouseName: '',
                attachment: '',
            },

            selectedSpouseCompany: ''

        });

    };

    handleRemove = (event) => {
        let data = this.state.dataSource;
        data.splice(event, 1);
        this.setState({
            dataSource: data
        });
    };


    render() {
        return (
            <div className="benefits benefit-funeral-add-new">
                <div className='row'>
                    <form className="form-group">
                        <div className="col-md-12">
                            <div><label htmlFor="employee-name" className="col-sm-12">Employee Name</label></div>
                            <div className="col-sm-11">
                                <Select
                                    options={this.state.funeralBenefitsData.employeeName}
                                    placeholder="Please Choose The Employee Name"
                                    onChange={this.handleEmployeeName}
                                    value={this.state.funeralBenefitsData.employeeName}
                                />
                            </div>
                        </div>

                        <div className="col-md-6">
                            <div><label htmlFor="designation" className="col-sm-12">Designation</label></div>
                            <div className="col-sm-10">
                                <input
                                    type="text"
                                    placeholder="Please Provide The Designation"
                                    className="form-control"
                                    onChange={this.handleDesignation}
                                    value={this.state.funeralBenefitsData.designation}
                                />
                            </div>
                        </div>


                        <div className="col-md-6">
                            <div><label htmlFor="dead-person" className="col-sm-12">Please Choose Whose Funeral Is It ? </label></div>
                            <div className="col-sm-10">
                                <Select
                                    placeholder="Please Choose An Option"
                                    options={deadPersonOptions}
                                    onChange={this.handleDeadPersonOptions}
                                    value={this.state.deadPersonOption}
                                />
                            </div>
                        </div>

                        <div className="col-md-6">
                            <div><label htmlFor="head-no" className="col-sm-12">Head Number</label></div>
                            <div className="col-sm-10">
                                <input
                                    type="number"
                                    placeholder="Please Provide The Head Number"
                                    className="form-control"
                                    onChange={this.handleHeadNo}
                                    value={this.state.funeralBenefitsData.headNo}
                                />
                            </div>
                        </div>


                        <div className="col-md-6">
                            <div><label htmlFor="attachment" className="col-sm-12 custom-file-label">Provide At Least One Or At Most Two
                                Attachment</label></div>
                            <div className="col-sm-10">

                                <CustomFileInput
                                    isMultiple="true"
                                    value={this.state.funeralBenefitsData.attachment}
                                    btnName="Upload"
                                    btnColor='#495057'
                                    btnBg='#e9ecef'
                                    onChange={this.handleAttachment}
                                />
                            </div>


                        </div>


                    </form>

                </div>

                <div className="row save-btn">
                    <div className="float-right">
                        <div>
                            <button className="btn btn-info" type="button" onClick={this.save}>Save</button>
                        </div>

                    </div>
                </div>

                <hr/>

                <div className="row">
                    <div className="result-area col-md-12">
                        <table className="table table-bordered table-responsive">
                            <thead>
                            <tr>
                                <th>Employee Name</th>
                                <th>Designation</th>
                                <th>Dead Person</th>
                                <th>Head Number</th>
                                <th>Action</th>
                            </tr>
                            </thead>


                            <tbody>

                            {
                                (this.state.dataSource.length <= 0) ?
                                    ( <tr><td colSpan="8" className="text-center font-weight-bold text-white bg-danger">
                                        No Data To Show
                                    </td></tr> ) :
                                    (this.state.dataSource.map((item, index) => {
                                            return(
                                                <tr key={index}>
                                                    <td>{item.employeeName}</td>
                                                    <td>{item.designation}</td>
                                                    <td>{item.deadPerson}</td>
                                                    <td>{item.headNo}</td>
                                                    <td>
                                                        <button className="btn btn-danger" onClick={this.handleRemove.bind(this, index)}>Remove</button>
                                                    </td>
                                                </tr>
                                            );
                                        })

                                    )
                            }
                            </tbody>

                        </table>
                    </div>
                </div>


            </div>
        )
    }
}

export default BenefitFuneralAddNew;
