import React, {Component} from 'react';
import Select from 'react-select';
import CustomFileInput from '../CustomFileInput';
import '../../Benefits/Benefits.css';
import { ToastContainer, toast } from 'react-toastify';
import {main_url} from "../../../utils/CommonFunction";

const SpouseCompanyOptions = [
    {value: 1, label: 'Yes'},
    {value: 2, label: 'No'}
];

class BenefitWeddingAddNew extends Component {
    constructor() {
        super();
        this.state = {
            weddingBenefitsData: {
                employeeName: [],
                designation: '',
                spouseName: '',
                employee_id:'',
                spouseCompanyOption: '',
            },
            dataSource: [],
            selectedSpouseCompany: ''
        }
    }

    componentDidMount() {

        fetch(`${main_url}staff_loan/getEmployeeList`)
            .then(res => { if (res.ok) return res.json() })
            .then(list => {
                this.setState({
                    employeeName: list
                })
            })
    }

    handleEmployeeName = (event) => {
        let data = this.state.weddingBenefitsData;
        data.employeeName = event.label;
        data.employee_id=event.value;
        this.setState({
            weddingBenefitsData: data
        });
    };

    handleDesignation = (event) => {
        let data = this.state.weddingBenefitsData;
        data.designation = event.target.value;
        this.setState({
            weddingBenefitsData: data
        });
    };

    handleSpouseName = (event) => {
        let data = this.state.weddingBenefitsData;
        data.spouseName = event.target.value;
        this.setState({
            weddingBenefitsData: data
        });
    };

    handleSpouseCompanyOptions = (event) => {
        let data = this.state.weddingBenefitsData;
        data.spouseCompanyOption = event.label;
        this.setState({
            weddingBenefitsData: data,
            selectedSpouseCompany: event
        });
    };

    checkFiles(e) {
        var files = document.getElementById("attach_file").files;
        var attachment = [];
        if (files.length > 5) {
            toast.warning('You can only upload a maximum of 5 files!')
        }
        else {
            for (let i = 0; i < files.length; i++) {
                attachment.push(files[i])
            }
        }
        this.setState({
            attachment: attachment
        })
    }
    save() {
        var data = {
            guarantor_id: this.state.selected_guarantor.value,
            recommended_by: this.state.selected_recommended.value,
            applicant_id: this.state.user_id,
            family_member_name: this.state.family_member_name,
            family_member_nrc: this.state.family_member_nrc,
            institution_name: this.state.institution_name,
            outstanding_amount: this.state.outstanding_amount,
            installment_amount: this.state.installment_amount,
            installment_term: this.state.installment_term,
            maturity_date: this.state.maturity_date,
            amount_requested: this.state.amount_requested,
            repayment_period: this.state.repayment_period,
            loan_purpose: this.state.loan_purpose,
            salary_advance: this.state.salary_advance,
            personal_loan: this.state.personal_loan,
            collateral_loan: this.state.collateral_loan,
            other_outstanding_debts: this.state.other_outstanding_debts,
            target_achieve: this.state.target_achieve,
            performance: this.state.performance,
            comment: this.state.comment,
            term_and_condition: this.state.term_and_condition
        }

        const formdata = new FormData();

        var obj = document.querySelector("#attach_file").files.length;
        for (var i = 0; i < obj; i++) {
            var imagedata = document.querySelector("#attach_file").files[i];
            formdata.append('uploadfile', imagedata);
        }

        formdata.append('wedding_benefit', JSON.stringify(data))
        let status = 0;
        fetch(`${main_url}wedding_benefit/saveWeddingBenefit`, {
            method: "POST",
            body: formdata
        })
            .then(res => {
                status = res.status;
                return res.text()
            })
            .then(text => {
                if (status === 200) toast.success(text);

                else toast.error(text);


            })
    }

    add = () => {
        let data = this.state.dataSource;
        data.push(this.state.weddingBenefitsData);

        this.setState({
            dataSource: data,

            weddingBenefitsData: {
                employeeName: [],
                designation: '',
                spouseName: '',
                attachment: '',
            },

            selectedSpouseCompany: ''

        });

    };

    handleRemove = (event) => {
        let data = this.state.dataSource;
        data.splice(event, 1);
        this.setState({
            dataSource: data
        });
    };


    render() {
        return (
            <div className="container">
                <div className='row'>
                    <form>
                        <div className="form-group col-md-12">
                            <div><label htmlFor="employee-name" className="col-sm-12">Employee Name</label></div>
                            <div className="col-sm-11">
                                <Select
                                    options={this.state.employeeName}
                                    placeholder="Please Choose Employee Name"
                                    onChange={this.handleEmployeeName}
                                />
                            </div>
                        </div>

                        <div className="form-group col-md-6">
                            <div><label htmlFor="designation" className="col-sm-12">Designation</label></div>
                            <div className="col-sm-10">
                                <input
                                    type="text"
                                    placeholder="Please Provide The Designation"
                                    className="form-control"
                                    onChange={this.handleDesignation}
                                    value={this.state.weddingBenefitsData.designation}
                                />
                            </div>
                        </div>


                        <div className="form-group col-md-6">
                            <div><label htmlFor="spouse-name" className="col-sm-12">Spouse Name</label></div>
                            <div className="col-sm-10">
                                <input
                                    type="text"
                                    placeholder="Please Provide The Employee Name"
                                    className="form-control"
                                    onChange={this.handleSpouseName}
                                    value={this.state.weddingBenefitsData.spouseName}
                                />
                            </div>
                        </div>

                        <div className="form-group col-md-6">
                            <div><label htmlFor="employee-name" className="col-sm-12">Is Your Spouse An Alliance Staff
                                ?</label></div>
                            <div className="col-sm-10">
                                <Select
                                    placeholder="Please Choose An Option"
                                    options={SpouseCompanyOptions}
                                    onChange={this.handleSpouseCompanyOptions}
                                    value={this.state.selectedSpouseCompany}
                                />
                            </div>
                        </div>
                        <div className="form-group col-12 col-sm-6 col-lg-3 col-xl-3">
                            <label className='col-sm-12'>Attachment</label>
                            <input className="full_width col-sm-10" type="file" id="attach_file" multiple onChange={this.checkFiles.bind(this)}></input>
                        </div>

                    </form>

                </div>

                <div className="row save-btn">
                    <div className="float-right">
                        <div>
                            <button className="btn btn-info" type="button" onClick={this.add}>Save</button>
                        </div>

                    </div>
                </div>

                <hr/>

                <div className="row">
                    <div className="result-area col-md-12">
                        <table className="table table-bordered table-responsive">
                            <thead>
                                <tr>
                                    <th>Employee Name</th>
                                    <th>Designation</th>
                                    <th>Spouse Name</th>
                                    <th>Is Spouse An Alliance Employee</th>
                                    <th>Action</th>
                                </tr>
                            </thead>


                            <tbody>

                                    {
                                        (this.state.dataSource.length <= 0) ?
                                            ( <tr><td colSpan="8" className="text-center font-weight-bold text-white bg-danger">
                                                No Data To Show
                                            </td></tr> ) :
                                            (this.state.dataSource.map((item, index) => {
                                                return(
                                                    <tr key={index}>
                                                        <td>{item.employeeName}</td>
                                                        <td>{item.designation}</td>
                                                        <td>{item.spouseName}</td>
                                                        <td>{item.spouseCompanyOption}</td>
                                                        <td>
                                                            <button className="btn btn-danger" onClick={this.handleRemove.bind(this, index)}>Remove</button>
                                                        </td>
                                                    </tr>
                                                );
                                            })

                                        )
                                    }
                            </tbody>

                        </table>
                    </div>
                </div>

                <div className="col-md-12 btn-rightend">
                                 <button className="btn btn-primary" ><span>Confirm</span> </button>
                             </div>
            </div>
        )
    }
}

export default BenefitWeddingAddNew;
